import React, { useState, useEffect } from "react";
import { url_get_monitoring } from "./../Routes";
import { useHistory } from 'react-router-dom';


import '../general.css'

const ServerStatus = () => {

    const history = useHistory();
    const [imageLink, setImageLink] = useState('');

    useEffect(() => {

        var requestOptions = {
            method: 'GET',
            redirect: 'follow'
        };

        fetch(url_get_monitoring, requestOptions)
            .then(response => response.text())
            .then((result) => {
                let link = "data:image/png;base64," + result
                setImageLink(link)
            })
            .catch(error => console.log('error', error));

    }, [])

    const handleExitClick = () => {
        history.push('/monitoring')
    }

    return (
        <div className={'Full'}>
            <div className={'Console'}>
                <img src={imageLink} alt="graph" width="700"/>
            </div>
            <button type="submit"  onClick={handleExitClick}> Exit </button><br></br>
        </div>


    )
}



export default ServerStatus
