import React, { useState, useEffect } from "react";
import { url_get_monitoring } from "./../Routes";

import '../general.css'

const ServerStatus = () => {

    const [imageLink, setImageLink] = useState('');

    useEffect(() => {

        var requestOptions = {
            method: 'GET',
            redirect: 'follow'
        };

        fetch(url_get_monitoring, requestOptions)
            .then(response => response.text())
            .then((result) => {
                let link = "data:image/png;base64," + result
                setImageLink(link)
            })
            .catch(error => console.log('error', error));

    }, [])

    return (
        <img src={imageLink} alt="graph" />

    )
}



export default ServerStatus
